mapper.select:
select

from product


      and price  >=  #{price1}


      and price  <=  #{price2}


      and name like CONCAT('%',#{name},'%')


    and categoryLevel1Id = #{categoryId} or categoryLevel2Id = #{categoryId} or categoryLevel3Id = #{categoryId}

select
count(1)
from product


      and price  >=  #{price1}


      and price  <=  #{price2}


      and name like CONCAT('%',#{name},'%')


    and categoryLevel1Id = #{categoryId} or categoryLevel2Id = #{categoryId} or categoryLevel3Id = #{categoryId}
